import{_ as r}from"./plugin-vue_export-helper-c27b6911.js";import{r as o,o as n,c as s,a,b as e,d as c,e as i}from"./app-4f0865ac.js";const l="/assets/3791e575c48b3698be6a94ae1dbff79d-56ea5949.png",d={},m={id:"flex布局-https-www-runoob-com-w3cnote-flex-grammar-html",tabindex:"-1"},h=a("a",{class:"header-anchor",href:"#flex布局-https-www-runoob-com-w3cnote-flex-grammar-html","aria-hidden":"true"},"#",-1),f={href:"https://www.runoob.com/w3cnote/flex-grammar.html",target:"_blank",rel:"noopener noreferrer"},p=i('<h3 id="基本概念" tabindex="-1"><a class="header-anchor" href="#基本概念" aria-hidden="true">#</a> 基本概念</h3><p>参考菜鸟教程的</p><p>采用Flex布局的元素，称为Flex容器（flex container），简称”容器”。它的所有子元素自动成为容器成员，称为Flex项目（flex item），简称”项目”。</p><figure><img src="'+l+'" alt="img" tabindex="0" loading="lazy"><figcaption>img</figcaption></figure><p>容器默认存在两根轴：水平的主轴（main axis）和垂直的交叉轴（cross axis）。主轴的开始位置（与边框的交叉点）叫做main start，结束位置叫做main end；交叉轴的开始位置叫做cross start，结束位置叫做cross end。</p><p>项目默认沿主轴排列。单个项目占据的主轴空间叫做main size，占据的交叉轴空间叫做cross size。</p><p>当一个div的display属性被设置为flex后，那么这个div内部的元素的display属性将变成inline-block</p><h3 id="容器属性" tabindex="-1"><a class="header-anchor" href="#容器属性" aria-hidden="true">#</a> 容器属性</h3>',8);function _(x,w){const t=o("ExternalLinkIcon");return n(),s("div",null,[a("h2",m,[h,e(" [flex布局](("),a("a",f,[e("https://www.runoob.com/w3cnote/flex-grammar.html"),c(t)]),e(" ))")]),p])}const u=r(d,[["render",_],["__file","css笔记.html.vue"]]);export{u as default};
